import React, { useEffect, useState } from "react";
import Breadcrumb from "react-bootstrap/Breadcrumb";
import { readDeck, listCards } from "./../utils/api";
import { useParams } from "react-router-dom";
import StudyCard from "./StudyCard";

const Study = () => {
  const [studyDisplay, setStudyDisplay] = useState({});
  const [deckCards, setDeckCards] = useState([]);
  const { deckId } = useParams();

  useEffect(() => {
    readDeck(deckId).then(setStudyDisplay);
    listCards(deckId).then(setDeckCards);
  }, [deckId]);

  return (
    <div>
      <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item">
            <a href="#">Home</a>
          </li>
          <li class="breadcrumb-item">
            <a href="#">Library</a>
          </li>
          <li class="breadcrumb-item active" aria-current="page">
            Data
          </li>
        </ol>
      </nav>
      {/* <Breadcrumb>
        <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
        <Breadcrumb.Item href={`/decks/${deckId}/`}>
          {studyDisplay.name}
        </Breadcrumb.Item>
        <Breadcrumb.Item active>Study</Breadcrumb.Item>
      </Breadcrumb> */}
      <br />
      <h2>Study: {studyDisplay.name}</h2>
      <br />
      <StudyCard
        deckCards={deckCards}
        studyDisplay={studyDisplay}
        deckId={deckId}
      />
    </div>
  );
};

export default Study;
