import React, { useState, useEffect } from "react";
import {
  listCards,
  readDeck,
  deleteCard,
  deleteDeck,
} from "./../utils/api/index";
import { useParams, useHistory, Link } from "react-router-dom";

const ViewDeck = () => {
  const history = useHistory();
  const { deckId } = useParams();
  const [deck, setDeck] = useState([]);
  const [cards, setCards] = useState([]);

  useEffect(() => {
    readDeck(deckId).then(setDeck);
    listCards(deckId).then(setCards);
  }, [deckId]);

  async function deleteHandler(cardId) {
    const deleteAlert = window.confirm(
      "You will not be able to recover the card!"
    );
    if (deleteAlert) {
      deleteCard(cardId);
      window.location.reload();
    }
  }

  async function deckDeleteHandler(id) {
    const deleteAlert = window.confirm(
      "You will not be able to recover the deck!"
    );
    if (deleteAlert) {
      deleteDeck(id);
      history.push("/");
    }
  }

  const cardDisplay = cards.map((card, index) => (
    <div class="card">
      <div class="card-body">
        <b>Front:</b> {card.front} <br />
        <b>Back:</b> {card.back} <br /> <br />
        <Link to={`/decks/${deckId}/cards/${card.id}/edit`}>
          <button class="btn btn-secondary">Edit</button>{" "}
        </Link>
        <button
          class="btn btn-danger float-right"
          onClick={() => deleteHandler(card.id)}
        >
          Delete
        </button>{" "}
      </div>
    </div>
  ));
  return (
    <div>
      <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item">
            <a href="/">Home</a>
          </li>
          <li class="breadcrumb-item active" aria-current="page">
            <b>{deck.name}</b>: Deck View
          </li>
        </ol>
      </nav>
      <br />
      <h3>{deck.name}</h3>
      <p>{deck.description}</p>
      <Link to={`/decks/${deckId}/edit`}>
        <button class="btn btn-secondary">Edit</button>{" "}
      </Link>
      <Link to={`/decks/${deckId}/study`}>
        <button href={`/decks/${deckId}/study`} class="btn btn-primary">
          Study
        </button>{" "}
      </Link>
      <Link to={`/decks/${deckId}/cards/new`}>
        <button class="btn btn-primary">+ Add Cards</button>{" "}
      </Link>
      <button
        variant="danger "
        class="btn btn-danger float-right"
        onClick={() => deckDeleteHandler(deck.id)}
      >
        Delete
      </button>
      <hr />
      <h2>Cards:</h2>
      <div>{cardDisplay}</div>
    </div>
  );
};

export default ViewDeck;
