import React, { useState, useEffect } from "react";
import Breadcrumb from "react-bootstrap/Breadcrumb";
import Form from "react-bootstrap/Form";
import { useParams, useHistory } from "react-router-dom";
import { readDeck } from "../utils/api/index";
import { createCard } from "../utils/api/index";

const CreateEditCard = () => {
  let history = useHistory();
  const [deck, setDeck] = useState([]);
  const { deckId } = useParams();
  const [cardFront, setCardFront] = useState("");
  const [cardBack, setCardBack] = useState("");
  const newCard = { front: cardFront, back: cardBack };

  useEffect(() => {
    readDeck(deckId).then(setDeck);
  }, [deckId]);

  function goToDeck() {
    history.push(`/decks/${deckId}`);
  }
  const frontSideChangeHandler = (event) => {
    setCardFront(event.target.value);
  };
  const backSideChangeHandler = (event) => {
    setCardBack(event.target.value);
  };

  async function saveCardHandler() {
    await createCard(deckId, newCard);
    goToDeck();
  }

  return (
    <div>
      <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item">
            <a href="/">Home</a>
          </li>
          <li class="breadcrumb-item">
            <a href="#">Library</a>
          </li>
          <li class="breadcrumb-item active" aria-current="page">
            Data
          </li>
        </ol>
      </nav>
      {/* <Breadcrumb>
        <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
        <Breadcrumb.Item href={`/decks/${deckId}/`}>
          {deck.name}
        </Breadcrumb.Item>
        <Breadcrumb.Item active>Add Card</Breadcrumb.Item>
      </Breadcrumb> */}
      <br />
      <h2>{deck.name}: Create Card Form</h2>
      <br />
      <Form>
        <Form.Group>
          <Form.Label>Front:</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            size="lg"
            type="text"
            placeholder="Front Side of Card"
            onChange={frontSideChangeHandler}
          />
          <br />
        </Form.Group>
        <Form.Group controlId="exampleForm.ControlTextarea1">
          <Form.Label>Back:</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            size="lg"
            placeholder="Back Side of Card"
            onChange={backSideChangeHandler}
          />
        </Form.Group>
      </Form>
      <a class="btn btn-primary" href={`/decks/${deckId}`} role="button">
        Done
      </a>{" "}
      <button type="button" class="btn btn-success" onClick={saveCardHandler}>
        Save Card
      </button>
    </div>
  );
};

export default CreateEditCard;
