import React, { useEffect } from "react";
import { listDecks } from "./../utils/api/index";
import { deleteDeck } from "./../utils/api/index";
import { Link } from "react-router-dom";

const DeckDisplay = ({ decks, setDecks }) => {
  // state use the then of the promise to set state
  // tie state to a prop of the component to auto rerender when state changes.
  /*
    let [s, setS] = useState([]);
    useEffect(() => {
      listDecks().then(setS);
    },[]);
   */

  // function(){}
  // async function(){}
  // ()=>{}
  // async ()=>{}

  useEffect(() => {
    listDecks().then(setDecks);
  }, [setDecks]);

  async function deleteHandler(id) {
    const deleteAlert = window.confirm(
      "You will not be able to recover the deck!"
    );
    if (deleteAlert) {
      deleteDeck(id);
      window.location.reload();
    }
  }

  return (
    <div>
      {decks.map((deck, index) => (
        // <Card key={index} style={{ width: "50rem" }}>
        //   <Card.Body>
        //     <Card.Title>{deck.name}</Card.Title>
        //     <Card.Text>{deck.description}</Card.Text>
        //     <Link to={`decks/${deck.id}`}>
        //       <Button variant="secondary">View</Button>{" "}
        //     </Link>
        //     <Link to={`decks/${deck.id}/study`}>
        //       <Button variant="primary">Study</Button>{" "}
        //     </Link>
        //     <Button variant="danger" onClick={() => deleteHandler(deck.id)}>
        //       Delete
        //     </Button>
        //   </Card.Body>
        // </Card>
        <div class="card" key={index} style={{ width: "50rem" }}>
          <div class="card-body">
            <h5 class="card-title">{deck.name}</h5>
            <p class="card-text">{deck.description}</p>
            <Link to={`decks/${deck.id}`}>
              <button type="button" class="btn btn-secondary">
                View
              </button>{" "}
            </Link>
            <Link to={`decks/${deck.id}/study`}>
              <button type="button" class="btn btn-primary">
                Study
              </button>{" "}
            </Link>
            <button
              type="button"
              class="btn btn-danger"
              onClick={() => deleteHandler(deck.id)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default DeckDisplay;
