import React, { useState, useEffect } from "react";
import {
  listCards,
  readDeck,
  deleteCard,
  deleteDeck,
} from "./../utils/api/index";
import { useParams, useHistory, Link } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Breadcrumb from "react-bootstrap/Breadcrumb";
import Card from "react-bootstrap/Card";

const ViewDeck = () => {
  const history = useHistory();
  const { deckId } = useParams();
  const [deck, setDeck] = useState([]);
  const [cards, setCards] = useState([]);

  useEffect(() => {
    readDeck(deckId).then(setDeck);
    listCards(deckId).then(setCards);
  }, [deckId]);

  async function deleteHandler(cardId) {
    const deleteAlert = window.confirm(
      "You will not be able to recover the card!"
    );
    if (deleteAlert) {
      deleteCard(cardId);
      window.location.reload();
    }
  }

  async function deckDeleteHandler(id) {
    const deleteAlert = window.confirm(
      "You will not be able to recover the deck!"
    );
    if (deleteAlert) {
      deleteDeck(id);
      history.push("/");
    }
  }

  const cardDisplay = cards.map((card, index) => (
    <div key={index}>
      <Card>
        <Card.Body>
          <b>Front:</b> {card.front} <br />
          <b>Back:</b> {card.back} <br /> <br />
          <Link to={`/decks/${deckId}/cards/${card.id}/edit`}>
            <Button variant="secondary">Edit</Button>{" "}
          </Link>
          <Button variant="danger" onClick={() => deleteHandler(card.id)}>
            Delete
          </Button>{" "}
        </Card.Body>
      </Card>
    </div>
  ));
  return (
    <div>
      <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item">
            <a href="/">Home</a>
          </li>
          <li class="breadcrumb-item active" aria-current="page">
            <b>{deck.name}</b>: Deck View
          </li>
        </ol>
      </nav>
      {/* <Breadcrumb>
        <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
        <Breadcrumb.Item active>
          <b>{deck.name}</b>: Deck View
        </Breadcrumb.Item>
      </Breadcrumb> */}
      <br />
      <h3>{deck.name}</h3>
      <p>{deck.description}</p>
      <Link to={`/decks/${deckId}/edit`}>
        <Button variant="secondary">Edit</Button>{" "}
      </Link>
      <Button href={`/decks/${deckId}/study`} variant="primary">
        Study
      </Button>{" "}
      <Button href={`/decks/${deckId}/cards/new`} variant="primary">
        + Add Cards
      </Button>{" "}
      <Button
        variant="danger "
        className="float-right"
        onClick={() => deckDeleteHandler(deck.id)}
      >
        Delete
      </Button>
      <hr />
      <h2>Cards:</h2>
      <div>{cardDisplay}</div>
    </div>
  );
};

export default ViewDeck;
