import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import { Link, useHistory } from "react-router-dom";

const StudyCard = ({ deckCards, deckId }) => {
  const [isActive, setIsActive] = useState(false);
  const [cardIndex, setCardIndex] = useState(0);
  let history = useHistory();

  const clickHandler = () => {
    setIsActive(!isActive);
  };

  const lessThanThree = (
    <div>
      <h2>Not enough cards</h2>
      <p>
        You need at least 3 cards to study. There are only{" "}
        <code>{deckCards.length}</code> cards in this deck.
      </p>
      <Link to={`/decks/${deckId}/cards/new`}>
        <Button>+ Add Cards</Button>
      </Link>
    </div>
  );

  function windowAlert() {
    const restartAlert = window.confirm(
      "Restart cards? Click cancel to return to the home page"
    );
    if (restartAlert) {
      setCardIndex(0);
    } else {
      history.push("/");
    }
  }

  function nextButtonHandler() {
    setIsActive(!isActive);
    setCardIndex(cardIndex + 1);
    if (cardIndex + 1 === deckCards.length) {
      windowAlert();
    }
  }

  const deckCardsMapped = deckCards.map((card, index) => (
    <div>
      <div class="card" key={index} style={{ width: "32rem" }}>
        <div class="card-body">
          <h5 class="card-title">
            Card <code>{index + 1}</code> of <code>{deckCards.length}</code>
          </h5>
          <p class="card-text">{isActive ? card.back : card.front}</p>
          <button type="button" class="btn btn-primary" onClick={clickHandler}>
            Flip
          </button>
          <button
            type="button"
            class="btn btn-secondary"
            style={isActive ? { display: "inline" } : { display: "none" }}
            onClick={nextButtonHandler}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  ));

  return (
    <div>
      {deckCards.length < 3 ? (
        <p>{lessThanThree}</p>
      ) : (
        <div>{deckCardsMapped[cardIndex]}</div>
      )}
    </div>
  );
};
export default StudyCard;
