import React, { useEffect, useState } from "react";
import { listDecks } from "./../utils/api/index";
import { deleteDeck } from "./../utils/api/index";
import { Link } from "react-router-dom";

const DeckDisplay = () => {
  const [decks, setDecks] = useState([]);
  const [cards, setCards] = useState();

  useEffect(() => {
    async function callDeck() {
      await listDecks().then(setDecks);
      await setCards(decks.cards);
    }
    callDeck();
  }, [setDecks]);

  function deleteHandler(id) {
    const deleteAlert = window.confirm(
      "You will not be able to recover the deck!"
    );
    if (deleteAlert) {
      deleteDeck(id);
      window.location.reload();
    }
  }

  return (
    <div>
      <Link to="/decks/new">
        <button type="button" className="btn btn-primary">
          + Create Deck
        </button>
      </Link>
      {cards.map((deck, index) => (
        <div className="card" key={index} style={{ width: "50rem" }}>
          <div className="card-body">
            <h5 className="card-title">
              {deck.name}{" "}
              <div className="float-right">{deck.cards.length} cards</div>
            </h5>
            <p className="card-text">{deck.description}</p>
            <Link to={`decks/${deck.id}`}>
              <button type="button" className="btn btn-secondary">
                View
              </button>{" "}
            </Link>
            <Link to={`decks/${deck.id}/study`}>
              <button type="button" className="btn btn-primary">
                Study
              </button>{" "}
            </Link>
            <button
              type="button"
              className="btn btn-danger"
              onClick={() => deleteHandler(deck.id)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default DeckDisplay;
