import React, { useState, useEffect } from "react";
import Breadcrumb from "react-bootstrap/Breadcrumb";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Link, useHistory, useParams } from "react-router-dom";
import { readDeck, updateDeck } from "./../utils/api/index";

const EditDeck = () => {
  let history = useHistory();
  const { deckId } = useParams();
  const [deck, setDeck] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    readDeck(deckId).then(setDeck);
  }, [deckId]);

  useEffect(() => {
    setTitle(title);
    setDescription(description);
  }, [description, title]);

  function goToDeck() {
    history.push(`/decks/${deckId}`);
  }
  async function updateDeckHandler() {
    await updateDeck({ name: title, description: description, id: deck.id });
    goToDeck();
  }

  function titleChangeHandler(event) {
    const newTitle = event.target.value;
    setTitle(newTitle);
  }
  function descriptionChangeHandler(event) {
    const newDescription = event.target.value;
    setDescription(newDescription);
  }

  return (
    <div>
      <Breadcrumb>
        <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
        <Breadcrumb.Item href={`/decks/${deckId}`}>{deck.name}</Breadcrumb.Item>
        <Breadcrumb.Item active>Create Deck</Breadcrumb.Item>
      </Breadcrumb>
      <h3>Edit Deck</h3>
      <br />
      <br />
      <p>Name</p>
      <Form.Group>
        <Form.Control
          size="lg"
          type="text"
          defaultValue={deck.name}
          onChange={titleChangeHandler}
        />
        <br />
      </Form.Group>
      <Form.Group>
        <Form.Label>Description</Form.Label>
        <Form.Control
          size="lg"
          as="textarea"
          rows={3}
          defaultValue={deck.description}
          onChange={descriptionChangeHandler}
        />
      </Form.Group>
      <Link to="/">
        <Button variant="secondary">Cancel</Button>{" "}
      </Link>
      <Button variant="primary" onClick={updateDeckHandler}>
        Submit
      </Button>{" "}
    </div>
  );
};

export default EditDeck;
