import React, { useState, useEffect } from "react";
import Breadcrumb from "react-bootstrap/Breadcrumb";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { useParams, useHistory } from "react-router-dom";
import { readDeck, readCard } from "../utils/api/index";
import { updateCard } from "../utils/api/index";

const EditCard = () => {
  let history = useHistory();
  const [deck, setDeck] = useState([]);
  const { deckId, cardId } = useParams();
  const [card, setCard] = useState([]);
  const [cardFront, setCardFront] = useState("");
  const [cardBack, setCardBack] = useState("");

  useEffect(() => {
    readDeck(deckId).then(setDeck);
  }, [deckId]);

  useEffect(() => {
    readCard(cardId).then(setCard);
  }, [cardId]);

  function goToDeck() {
    history.push(`/decks/${deckId}`);
  }

  function frontChangeHandler(event) {
    const newFront = event.target.value;
    setCardFront(newFront);
  }
  function backChangeHandler(event) {
    const newBack = event.target.value;
    setCardBack(newBack);
  }

  async function saveHandler() {
    const newCard = {
      id: card.id,
      front: cardFront,
      back: cardBack,
      deckId: deckId,
    };
    console.log("saveHandler newCard: ", newCard);
    await updateCard(newCard);
    goToDeck();
  }

  return (
    <div>
      <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item">
            <a href="#">Home</a>
          </li>
          <li class="breadcrumb-item">
            <a href="#">Library</a>
          </li>
          <li class="breadcrumb-item active" aria-current="page">
            Data
          </li>
        </ol>
      </nav>
      {/* <Breadcrumb>
        <Breadcrumb.Item href="/">Home</Breadcrumb.Item>
        <Breadcrumb.Item href={`/decks/${deckId}/`}>
          {deck.name}
        </Breadcrumb.Item>
        <Breadcrumb.Item active>Edit Card</Breadcrumb.Item>
      </Breadcrumb> */}
      <br />
      <h2>{deck.name}: Edit Card Form</h2>
      <br />
      <Form>
        <Form.Group>
          <Form.Label>Front:</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            size="lg"
            type="text"
            value={card.front}
            onChange={frontChangeHandler}
          />
          <br />
        </Form.Group>
        <Form.Group controlId="exampleForm.ControlTextarea1">
          <Form.Label>Back:</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            size="lg"
            value={card.back}
            onChange={backChangeHandler}
          />
        </Form.Group>
      </Form>
      <Button href={`/decks/${deckId}`}>Done</Button>{" "}
      <Button variant="success" onClick={saveHandler}>
        Save Card
      </Button>{" "}
    </div>
  );
};

export default EditCard;
